<script>
import AppState from './../../AppState.js'

export default {
    props: {
        barName: {
            type: String,
            default: 'Bar Assistant'
        },
        barSubtitle: {
            type: String,
            default: 'Your personal bar assistant'
        },
        noLink: {
            type: Boolean,
            default: false
        }
    },
    data() {
        return {
            appState: new AppState()
        }
    },
    computed: {
        name() {
            return this.appState.bar.name || this.barName || 'Bar Assistant'
        },
        description() {
            return this.appState.bar.subtitle || this.barSubtitle || 'Your personal bar assistant'
        },
        element() {
            return this.noLink ? 'div' : 'RouterLink'
        }
    }
}
